import React, {Component} from "react";
import  ReactDOM, { render }  from "react-dom";
import "./styles.css";



class App extends Component{

    constructor(props){

        super(props)

        this.state = {
            clock: 1000,
            glass: 'water'
        }


        changeGlass = ()=>{
            this.setState({
                glass: 'coffee'
            })

        }
    }





    
}



const rootElement = document.getElementById("root");

ReactDOM.render(<App />, rootElement);
