import {useState} from 'react';


function SmartCounter(){

    const [valor, setValor] = useState(0)


    

    return(
        <>
            <h1>{valor}</h1>
            <button onClick={()=>setValor(valor+1)}>Aumentar</button>
            <button onClick={()=>setValor(valor+1)}>Aumentar</button>
        </>
    )
}

export default SmartCounter;
