import React, {useState} from "react";
import '../IfoodCounter/ifoodCounter.css'

function IfoodCounter(){
    
    const [qtd, setQtd] = useState(0)

    const[botaoStyle, setBotaoStyle] = useState('diminuiAtivo')

    function aumentar(){
        setQtd(qtd+1)
    }

    function diminuir(){
        if(qtd>0){
            setQtd(qtd-1)

        }else{
            setBotaoStyle()
        }
       
    }

    return(
        <div className="content">
            
            <h1>Ifood</h1>
            <button className="diminuiAtivo" onClick={diminuir}>-</button>
            <p>{qtd}</p>
            <button onClick={aumentar}>+</button>
        </div>
    )
}

export default IfoodCounter;
